import styled from "styled-components";
import { themeGet } from "@styled-system/theme-get";

type TextProps = {
  bold?: any;
};

const OrderRecivedWrapper = styled.div`
  background-color: ${themeGet("colors.dark", "#F7F7F7")};
  position: relative;
  padding: 0px 0 60px 0;
  min-height: 100vh;
`;

export const OrderRecivedContainer = styled.div`
  background-color: #fff;
  border: 1px solid ${themeGet("colors.borderColor", "#f1f1f1")};
  padding: 60px;
  border-radius: 6px;
  overflow: hidden;
  position: relative;
  width: calc(100% - 30px);
  margin-left: auto;
  margin-right: auto;
  @media (min-width: 991px) {
    width: 870px;
  }
  @media (max-width: 990px) {
    padding: 50px 45px;
  }
  @media (max-width: 767px) {
    padding: 50px 25px;
  }

  .home-btn {
    display: inline-flex;
    align-items: center;
    justify-content: center;
    font-family: "Lato", sans-serif;
    font-size: ${themeGet("fontSizes.2", "15")}px;
    font-weight: ${themeGet("fontWeights.3", "400")};
    color: ${themeGet("colors.darkRegular", "#77798c")};
    padding: 5px 15px;
    height: 36px;
    border: 1px solid #e6e6e6;
    border-radius: 6px;
    position: absolute;
    top: 15px;
    right: 15px;
    transition: 0.15s ease-in-out;
    @media (max-width: 767px) {
      font-size: 13px;
      height: 34px;
      padding: 5px 12px;
    }

    &:hover {
      background-color: ${themeGet("colors.primary", "#009E7F")};
      border-color: ${themeGet("colors.primary", "#009E7F")};
      color: #fff;
    }
  }
`;

export const ListPro = styled.div`
  background-color: #fff;
  border: 1px solid ${themeGet("colors.borderColor", "#f1f1f1")};

  border-radius: 6px;
  overflow: hidden;
  position: relative;
  margin-top: 15px;
  margin-left: auto;
  margin-right: auto;
  @media (min-width: 991px) {
    width: 870px;
  }
  @media (max-width: 990px) {
    padding: 50px 45px;
  }
  @media (max-width: 767px) {
    padding: 50px 25px;
  }

  .home-btn {
    display: inline-flex;
    align-items: center;
    justify-content: center;
    font-family: "Lato", sans-serif;
    font-size: ${themeGet("fontSizes.2", "15")}px;
    font-weight: ${themeGet("fontWeights.3", "400")};
    color: ${themeGet("colors.darkRegular", "#77798c")};

    height: 36px;
    border: 1px solid #e6e6e6;
    border-radius: 6px;
    position: absolute;

    transition: 0.15s ease-in-out;
    @media (max-width: 767px) {
      font-size: 13px;
      height: 34px;
      padding: 5px 12px;
    }

    &:hover {
      background-color: ${themeGet("colors.primary", "#009E7F")};
      border-color: ${themeGet("colors.primary", "#009E7F")};
      color: #fff;
    }
  }
`;

export const OrderInfo = styled.div`
  margin-bottom: 60px;
  @media (max-width: 767px) {
    margin-bottom: 30px;
  }
`;

export const OrderDetails = styled.div`
  margin-bottom: 60px;
  @media (max-width: 767px) {
    margin-bottom: 50px;
  }
`;

export const TotalAmount = styled.div``;

export const BlockTitle = styled.h2`
  font-family: "Poppins", sans-serif;
  font-size: ${themeGet("fontSizes.4", "21")}px;
  font-weight: ${themeGet("fontWeights.6", "700")};
  color: ${themeGet("colors.darkBold", "#0D1136")};
  line-height: 1;
  margin-bottom: 32px;

  @media (max-width: 767px) {
    font-size: 16px;
    margin-bottom: 25px;
  }
`;
export const BlockTit = styled.h2`
  font-family: "Poppins", sans-serif;
  font-size: ${themeGet("fontSizes.4", "21")}px;
  font-weight: ${themeGet("fontWeights.6", "700")};
  color: ${themeGet("colors.darkBold", "#0D1136")};
  line-height: 1;
  margin-bottom: 32px;
  display: flex;
  flex-direction: column;
  align-items: center;
  justify-content: center;
  margin-top: -5px;
  @media (max-width: 767px) {
    font-size: 16px;
    margin-bottom: 25px;
  }
`;

export const Pop = styled.div`
  width: 380px;
  height: 86.5vh;
  background-color: #ffffff;
  position: fixed;
  bottom: 0;

  margin-top: 50px;
  z-index: 1010;
  box-shadow: 0 21px 36px rgba(0, 0, 0, 0.16);
  transition: all 0.35s ease-in-out;

  @media (max-width: 430px) {
    width: 100%;
  }

  &.cartPopupFixed {
    right: 0;
  }
`;

export const Blocklist = styled.div`
  background-color: ${themeGet("colors.dark", "#f7f7f7")};
  line-height: 1;
  margin-bottom: 32px;

  top: 50%;
  margin-top: 10px;
  z-index: 99;
  @media (max-width: 767px) {
    font-size: 16px;
    margin-bottom: 25px;
  }
`;

export const Text = styled.p<TextProps>`
  font-family: "Lato", sans-serif;
  font-size: ${themeGet("fontSizes.2", "15")}px;
  color: ${themeGet("colors.darkBold", "#0D1136")};
  font-weight: ${(props) => (props.bold ? "700" : "400")};
  line-height: 1.2;
  display: block;
  margin: 0;

  &:last-child {
    color: ${themeGet("colors.darkMedium", "#424561")};
  }
`;

export const Txt = styled.p<TextProps>`
  font-family: "Lato", sans-serif;
  font-size: ${themeGet("fontSizes.2", "15")}px;
  color: ${themeGet("colors.darkBold", "#0D1136")};
  font-weight: ${(props) => (props.bold ? "700" : "400")};
  line-height: 1.2;

  margin: 0;
  display: flex;
  flex-direction: column;
  align-items: center;
  justify-content: center;

  &:last-child {
    color: ${themeGet("colors.darkMedium", "#424561")};
  }
`;

export const InfoBlockWrapper = styled.div`
  display: flex;
  align-items: center;
  justify-content: space-between;
  margin-top: 30px;
  @media (max-width: 767px) {
    flex-wrap: wrap;
  }
`;

export const InfoBlock = styled.div`
  padding: 0 15px;
  border-left: 1px solid ${themeGet("colors.borderColor", "#f1f1f1")};
  @media (max-width: 767px) {
    max-width: 100%;
    flex: 0 0 100%;
    margin-bottom: 20px;

    padding: 0;
    border: 0;
  }

  &:first-child {
    padding-left: 0;
    border-left: 0;
  }
  &:last-child {
    padding-right: 0;
  }

  .title {
    margin-bottom: 10px;
  }
`;

export const ListItem = styled.div`
  display: flex;
  margin-bottom: 20px;
  &:last-child {
    margin-bottom: 0;
  }
`;

export const ListTitle = styled.div`
  flex-basis: 210px;
  max-width: 210px;
  flex-shrink: 0;
  position: relative;
  @media (max-width: 767px) {
    flex-basis: 105px;
    max-width: 105px;
  }
  &:after {
    content: ":";
    position: absolute;
    top: -1px;
    right: -2px;
    line-height: 1;
  }
`;

export const ListDes = styled.div`
  padding-left: 90px;
  @media (max-width: 767px) {
    padding-left: 20px;
  }
`;

export default OrderRecivedWrapper;
