import React, { useReducer, useEffect } from "react";
import { SearchContext } from "./search.context";
type ActionType = { type: "UPDATE" | "RESET"; payload: object };

function reducer(state: any, action: ActionType): any {
  switch (action.type) {
    case "UPDATE":
      return { ...state, ...action.payload };
    case "RESET":
      return { page: 1 };
    default:
      return state;
  }
}

type SearchProviderProps = {
  query: any;
};

export const SearchProvider: React.FunctionComponent<SearchProviderProps> = ({
  children,
  query,
}) => {
  const [state, dispatch] = useReducer(reducer, { ...query, page: 1 });
  const updateSearch = (search) => {
    dispatch({ type: "UPDATE", payload: search });
    localStorage.setItem("search", search);
  };
  useEffect(() => {
    const search = localStorage.getItem("search");
    console.log(search);

    if (search || search === "undefined" || search === "null") {
      updateSearch(search);
    } else {
      updateSearch({ page: 1 });
    }
    console.log(search);
  }, []);
  return (
    <SearchContext.Provider value={{ state, dispatch }}>
      {children}
    </SearchContext.Provider>
  );
};
